import 'package:equatable/equatable.dart';import '../../../domain/entities/person_entity.dart';abstract class PersonState extends Equatable{ const PersonState();  @override  List<Object?> get props => [];}class PersonEmpty extends PersonState{}class PersonLoading extends PersonState{  final List<PersonEntity> oldPersonsList;  final bool isFirstFetch;  const PersonLoading(this.oldPersonsList,this.isFirstFetch);  @override  List<Object?> get props => [oldPersonsList,isFirstFetch];}class PersonLoaded extends PersonState{  final List<PersonEntity> list; const PersonLoaded(this.list);  @override  List<Object?> get props => [list];}class PersonFailure extends PersonState{  final String message; const PersonFailure(this.message); @override  List<Object?> get props => [message];}