part of 'search_bloc.dart';enum SearchStatus { empty, loading, loaded, failure, init }class SearchState extends Equatable {  final SearchStatus status;  final List<PersonEntity> person;  final String errorMessage;  const SearchState({this.person = const [], this.status = SearchStatus.init,this.errorMessage = ''});  SearchState copyWith({List<PersonEntity>? person, SearchStatus? status,String errorMessage= ''}) {    return SearchState(status: status ?? this.status, person: person ?? this.person,errorMessage: errorMessage??this.errorMessage);  }  @override  List<Object?> get props => [];}