import 'package:cached_network_image/cached_network_image.dart';import 'package:flutter/material.dart';class PersonCachedImage extends StatelessWidget {  final String imageUrl;  final double width;  final double height;  const PersonCachedImage({Key? key, required this.imageUrl, required this.width, required this.height})      : super(key: key);  Widget _imageWidget(ImageProvider provider) {    return DecoratedBox(      decoration: BoxDecoration(          borderRadius: const BorderRadius.only(topLeft: Radius.circular(8), bottomLeft: Radius.circular(8)),          image: DecorationImage(image: provider, fit: BoxFit.cover)),    );  }  @override  Widget build(BuildContext context) {    return CachedNetworkImage(      imageUrl: imageUrl,      height: height,      width: width,      imageBuilder: (context, imageProvider) {        return _imageWidget(imageProvider);      },      placeholder: (context, url) {        return const Center(          child: CircularProgressIndicator(),        );      },      errorWidget: (context, url, error) {        return _imageWidget(AssetImage('assets/images/noimage.jpg'));      },    );  }}