import 'package:flutter/material.dart';import 'package:ricky_morty/common/app_colors.dart';import 'package:ricky_morty/features/domain/entities/person_entity.dart';import 'package:ricky_morty/features/presentation/pages/person_detail_page.dart';import 'package:ricky_morty/features/presentation/widges/person_cached_network_image.dart';class PersonCard extends StatelessWidget {  final PersonEntity person;  const PersonCard({Key? key, required this.person}) : super(key: key);  @override  Widget build(BuildContext context) {    return GestureDetector(      onTap:(){        Navigator.of(context).push(MaterialPageRoute(builder: (_)=> PersonDetailPage(person: person,)));      },      child: Container(      padding: const EdgeInsets.symmetric(vertical: 8,horizontal: 4),      margin: const EdgeInsets.symmetric(horizontal: 8,vertical: 2),      decoration: BoxDecoration(        color: AppColors.callBackGround,        borderRadius: BorderRadius.circular(8),      ),      child: Row(        children: [          PersonCachedImage(imageUrl: person.image,height: 166,width: 166,),          const SizedBox(            width: 16,          ),          Expanded(              child: Column(                crossAxisAlignment: CrossAxisAlignment.start,                children: [                  const SizedBox(                    height: 12,                  ),                  Text(                    person.name,                    style: const TextStyle(fontSize: 20, color: Colors.white, fontWeight: FontWeight.w700),                  ),                  const   SizedBox(                    height: 4,                  ),                  Row(                    children: [                      Container(                        height: 8,                        width: 8,                        decoration: BoxDecoration(                            borderRadius: BorderRadius.circular(8),                            color: person.status == 'Alive' ? Colors.green : Colors.red),                      ),                      const SizedBox(                        width: 8,                      ),                      Expanded(                          child: Text(                            "${person.status} - ${person.species}",                            style: const TextStyle(color: Colors.white),                            maxLines: 1,                            overflow: TextOverflow.ellipsis,                          ))                    ],                  ),                  const   SizedBox(                    height: 12,                  ),                  Text('Last know location:',style: TextStyle(color:  AppColors.greyColor),maxLines: 1,overflow: TextOverflow.ellipsis,),                  const SizedBox(height: 4,),                  Text(person.location.name,style: TextStyle(color: Colors.white),maxLines: 1,overflow: TextOverflow.ellipsis,),                  const   SizedBox(                    height: 12,                  ),                  Text('Origin',style: TextStyle(color: AppColors.greyColor)),                  const   SizedBox(                    height: 4,                  ),                  Text(person.origin.name,style: TextStyle(color: Colors.white)),                  const   SizedBox(                    height: 16,                  ),                ],              )),          const SizedBox(width: 12,)        ],      ),    ),);  }}