import 'package:flutter/material.dart';import 'package:ricky_morty/features/domain/entities/person_entity.dart';import 'package:ricky_morty/features/presentation/widges/person_cached_network_image.dart';import '../pages/person_detail_page.dart';class SearchResult extends StatelessWidget {  final PersonEntity result;  const SearchResult({Key? key, required this.result}) : super(key: key);  @override  Widget build(BuildContext context) {    return GestureDetector(      onTap: () {        Navigator.push(          context,          MaterialPageRoute(            builder: (context) => PersonDetailPage(person: result),          ),        );      },      child: Card(        shape: RoundedRectangleBorder(          borderRadius: BorderRadius.circular(8),        ),        elevation: 2.0,        child: Column(          crossAxisAlignment: CrossAxisAlignment.start,          children: [            SizedBox(              height: 300,              width: double.infinity,              child: PersonCachedImage(                imageUrl: result.image, width: 100, height: 100,              ),            ),            Padding(              padding: const EdgeInsets.all(8.0),              child: Text(                result.name,                style: const TextStyle(                  fontWeight: FontWeight.bold,                  fontSize: 26.0,                ),              ),            ),            Padding(              padding: const EdgeInsets.all(8.0),              child: Text(                result.location.name,                style: const TextStyle(                  fontWeight: FontWeight.w400,                  fontSize: 16.0,                ),              ),            ),          ],        ),      ),    );;  }}